public class Mul99
{

    public static void main(String[] args)
    {
        int i,j,n=9;
        for(i=1;i<=n;i++)
        {
            for(j=1;j<=i;j++)
                System.out.print("  "+i*j);
            System.out.println("");
        }
    }
}
